import "./Quiz.css"
import React, { useState } from 'react';
import { useNavigate } from "react-router-dom"

function Quiz() {
	const questions = [
		{
			question: "How do you ensure that your financial affairs are in order should you pass?",
			a: "Budget",
			b: "Estate Planning",
			c: "Financial advisor",
			d: "Wealth management",
			correct: "a",
		},

		{

			questtion: "How do we prepare ourselves for a time when we can no longer work?",
            a: "Risk management",
			b: "Retirement plan",
			c: "Estate Planning",
			d: "Wealth management",
			correct: "b",

		},

		{
			question:"What do we call the things that we own?",
                a:"Assets",
                b:"Budget",
                c:"Having a financial plan",
                d:"Investments",
				correct:"a",
		},

		{
			question:"How do we ensure our money works for us?",
                a:"Buying gold",
                b:"Budget",
                c:"Having a financial plan",
                d:"Having investments",
				correct:"c",
		},

		{
			question:"What do we call the individual who advises clients on the best way to make money moves?",
                a:"An investment banker",
                b:"An insurance agent",
                c:"An investment advisor",
                d:"A financial advisor",
				correct:"d",
		},

		{
			question:"What's a detailed plan of income and expenses expected over a certain period of time?",
                a:"A budget",
                b:"A financial plan",
                c:"A Taxonomy",
                d:"Investments",
				correct:"a",
		},

		{
			question:"Who analyzes client's overall financial situations?",
                a:"An insurance Agent",
                b:"A budget Analyst",
                c:"A wealth Manager",
                d:"A Financial Planner",
				correct:"b",
		},

		{
			question:"The financial planning process consist of ___ steps",
                a:"1",
                b:"4",
                c:"6",
                d:"7",
				correct:"4",
		},

		{
			question:"What do we call a list of assets and liabilities of a specific point of time?",
                a:"Balance Sheet",
                b:"Cash Flow",
                c:"CPJ/CRJ",
                d:"Petty Cash",
				correct:"c",
		},

		{
			question:"Where do financial advisers store data?",
                a:"Word",
                b:"Database",
                c:"Excel",
                d:"PowerPoint",
				correct:"c",
		},


	];

	const quiz = document.getElementById('quiz')
	const answers = document.querySelectorAll('.answer')
	const questionS = document.getElementById('question')
	const a_text = document.getElementById('a_text')
	const b_text = document.getElementById('b_text')
	const c_text = document.getElementById('c_text')
	const d_text = document.getElementById('d_text')
	const submitBtn = document.getElementById('submit')

	let currentQuiz = 0;
	let score = 0;

	loadQuiz()

	function loadQuiz() {
		deselectAnswers()

		const currentQuizData = quizData[currentQuiz]

		questionS.innerText = currentQuizData.question
		a_text.innerText = currentQuizData.a
		b_text.innerText = currentQuizData.b
		c_text.innerText = currentQuizData.c
		d_text.innerText = currentQuizData.d
	}

	function deselectAnswers() {
		answers.forEach(answers => answers.checked = false)
	}

	function getSelected() {
		let answer
		answers.forEach(answers => {
			if (answers.checked) {
				answer = answers.id
			}
		})
		return answer
	}

	submitBtn.addEventListener('click', () => {
		const answer = getSelected()
		if (answer) {
			if (answer === quizData[currentQuiz].correct) {
				score++
			}
			currentQuiz++
			if (currentQuiz < quizData.length) {
				loadQuiz()
			} else {
				quiz.innerHTML = `
				<h2>You answerd ${score} /${quizData.length} questions correctly!</h2>
				`
			}

		}
	})
}


export default Quiz;
