import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from "react-router-dom"

let form = {}

function handleChange(e) {
	const { id, value } = e.target

	form[id] = value;
}

function Quiz() {
	let currentQuiz = 0;
	let score = 0;

	loadQuiz()

	const questionRef = React.useRef("hgfdxfcgvhbjnk");
